import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import Home from "./components/pages/home/Home";
import Dashboard from "./components/pages/admin/Dashboard";
import BrandPage from "./components/pages/admin/Brand/BrandPage";
import CategoryPage from "./components/pages/admin/Category/CategoryPage";
import ProductPage from "./components/pages/admin/Product/ProductPage";
import ProductDetail from "./components/pages/home/ProductDetail";
import ReviewPage from "./components/pages/admin/Review/ReviewPage";
import CartPage from "./components/pages/Cart/CartPage";
import OrderManagement from "./components/pages/admin/Order/OrderManagement";  // Admin Order Management
import OrderHistory from "./components/pages/home/OrderHistory";  // User Order History
import SearchResults from "./components/pages/home/SearchResults";
// Navbar component
const Navbar = ({ role, setRole }) => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("role");
        setRole("");
        navigate("/login");
    };

    return (
        <nav style={{ display: "flex", justifyContent: "space-between", padding: "10px", background: "#ddd", alignItems: "center" }}>
            <div>
                <button onClick={() => navigate("/")}>üè† Home</button>
                {role === "admin" && <button onClick={() => navigate("/admin")}>üîß Admin</button>}
                {role === "user" && <button onClick={() => navigate("/order-history")}>üìù L·ªãch s·ª≠ ƒë∆°n h√†ng</button>}  {/* Th√™m link t·ªõi trang l·ªãch s·ª≠ ƒë∆°n h√†ng */}
            </div>
            <div>
                <button onClick={() => navigate("/cart")}>üõí Gi·ªè h√†ng</button>
                {role ? (
                    <button onClick={handleLogout}>üö™ Logout</button>
                ) : (
                    <>
                        <button onClick={() => navigate("/login")}>üîë Login</button>
                        <button onClick={() => navigate("/register")}>üìù Register</button>
                    </>
                )}
            </div>
        </nav>
    );
};

// Main App component
const App = () => {
    const [role, setRole] = useState(localStorage.getItem("role") || "");
    const [cartItems, setCartItems] = useState(() => {
        return JSON.parse(localStorage.getItem("cart")) || [];
    });

    useEffect(() => {
        const handleStorageChange = () => setRole(localStorage.getItem("role") || "");
        window.addEventListener("storage", handleStorageChange);
        return () => window.removeEventListener("storage", handleStorageChange);
    }, []);

    const clearCart = () => {
        setCartItems([]);
        localStorage.removeItem("cart");
    };

    return (
        <Router>
            <Navbar role={role} setRole={setRole} />
            <Routes>
                {/* Auth Routes */}
                <Route path="/login" element={<Login setRole={setRole} />} />
                <Route path="/register" element={<Register />} />

                {/* User Routes */}
                <Route path="/" element={<Home />} />
                <Route path="/product/:id" element={<ProductDetail />} />
                <Route path="/cart" element={<CartPage cartItems={cartItems} clearCart={clearCart} />} />
                <Route path="/order-history" element={<OrderHistory />} /> {/* Trang l·ªãch s·ª≠ ƒë∆°n h√†ng cho ng∆∞·ªùi d√πng */}
                <Route path="/search-results" element={<SearchResults />} />

                {/* Admin Routes */}
                {role === "admin" ? (
                    <>
                        <Route path="/admin" element={<Dashboard />} />
                        <Route path="/admin/brands" element={<BrandPage />} />
                        <Route path="/admin/categories" element={<CategoryPage />} />
                        <Route path="/admin/products" element={<ProductPage />} />
                        <Route path="/admin/reviews" element={<ReviewPage />} />
                        <Route path="/admin/orders" element={<OrderManagement />} /> {/* Admin Order Management */}
                    </>
                ) : (
                    <Route path="/admin/*" element={<Navigate to="/" />} />
                )}

                {/* Fallback Route */}
                <Route path="*" element={<Navigate to="/" />} />
            </Routes>
        </Router>
    );
};

export default App;







