import React from "react";

const ProductList = ({ products, onEdit, onDelete }) => {
    return (
        <table className="product-table">
            <thead>
                <tr>
                    <th>Product Name</th>
                    <th>Price</th>
                    <th>Stock</th>
                    <th>Category</th>
                    <th>Brand</th>
                    <th>Image</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {products.map((product) => (
                    <tr key={product._id}>
                        <td>{product.name}</td>
                        <td>{product.price}</td>
                        <td>{product.stock}</td>
                        <td>{product.category?.name}</td>
                        <td>{product.brand?.name}</td>
                        <td>
                            {product.imageUrl && product.imageUrl.length > 0 ? (
                                <img src={`http://localhost:5000${product.imageUrl[0]}`} alt={product.name} width="50" />
                            ) : (
                                "No Image"
                            )}
                        </td>
                        <td>
                            <button className="edit-btn" onClick={() => onEdit(product)}>‚úèÔ∏è Edit</button>
                            <button className="delete-btn" onClick={() => onDelete(product._id)}>üóëÔ∏è Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default ProductList;
