import axios from "axios";

const API_URL = "http://localhost:5000/api/brands";

export const getBrands = async () => {
    const res = await axios.get(API_URL);
    return res.data;
};

export const createBrand = async (brandData, token) => {
    try {
        console.log("Dữ liệu gửi đi:", brandData); // Kiểm tra dữ liệu có hợp lệ không
        console.log("Token gửi đi:", token); // Kiểm tra token

        const res = await axios.post("http://localhost:5000/api/brands", brandData, {
            headers: {
                Authorization: `Bearer ${token}`,
                "Content-Type": "application/json",
            },
        });

        console.log("API Response:", res.data);
        return res.data;
    } catch (error) {
        console.error("Lỗi khi gửi dữ liệu:", error.response?.data || error.message);
        throw error;
    }
};



export const updateBrand = async (id, brandData, token) => {
    const res = await axios.put(`${API_URL}/${id}`, brandData, {
        headers: { Authorization: `Bearer ${token}` },
    });
    return res.data;
};

export const deleteBrand = async (id, token) => {
    const res = await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
    });
    return res.data;
};
