import React, { useState } from "react";

const ProductList = ({ products, onEdit, onDelete }) => {
    // Pagination state: current page and products per page (5)
    const [currentPage, setCurrentPage] = useState(1);
    const productsPerPage = 5;

    // Calculate index of the last product on the current page
    const indexOfLastProduct = currentPage * productsPerPage;
    // Calculate index of the first product on the current page
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    // Get the products for the current page
    const currentProducts = products.slice(indexOfFirstProduct, indexOfLastProduct);

    // Calculate total pages
    const totalPages = Math.ceil(products.length / productsPerPage);

    // Change page
    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    return (
        <div>
            <table className="product-table">
                <thead>
                    <tr>
                        <th>T√™n</th>
                        <th>Gi√°</th>
                        <th>T·ªìn Kho</th>
                        <th>M√¥ T·∫£</th>
                        <th>K√≠ch C·ª°</th>
                        <th>M√†u</th>
                        <th>·∫¢nh</th>
                        <th>H√†nh ƒê·ªông</th>
                    </tr>
                </thead>
                <tbody>
                    {currentProducts.length > 0 ? (
                        currentProducts.map((product) => (
                            <tr key={product._id}>
                                <td>{product.name}</td>
                                <td>{product.price}</td>
                                <td>{product.stock}</td>
                                <td>{product.description}</td>
                                <td>{product.size?.join(", ") || "N/A"}</td>
                                <td>{product.colors?.join(", ") || "N/A"}</td>
                                <td>
                                    {product.imageUrl && product.imageUrl.length > 0 ? (
                                        <img src={`http://localhost:5000${product.imageUrl[0]}`} alt={product.name} width="50" />
                                    ) : (
                                        "No Image"
                                    )}
                                </td>
                                <td>
                                    <button className="edit-btn" onClick={() => onEdit(product)}>‚úèÔ∏è S·ª≠a</button>
                                    <button className="delete-btn" onClick={() => onDelete(product._id)}>üóëÔ∏è X√≥a</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="8">No products found</td>
                        </tr>
                    )}
                </tbody>
            </table>

            {/* Pagination Controls */}
            <div className="pagination-controls">
                <button
                    disabled={currentPage === 1}
                    onClick={() => handlePageChange(currentPage - 1)}
                >
                    Previous
                </button>
                <span>
                    Page {currentPage} of {totalPages}
                </span>
                <button
                    disabled={currentPage === totalPages}
                    onClick={() => handlePageChange(currentPage + 1)}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default ProductList;
