// ✅ frontend/src/components/pages/Cart/CartPage.jsx
import React, { useEffect, useState } from "react";
import { createOrderWithDetails } from "../../services/orderService";

const CartPage = () => {
  const userId = localStorage.getItem("userId");
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [message, setMessage] = useState("");
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [note, setNote] = useState("");

  useEffect(() => {
    if (!userId) return;
    const stored = JSON.parse(localStorage.getItem(`cart_${userId}`)) || [];
    setCartItems(stored);
    setSelectedItems(stored.map(item => item._id));
  }, [userId]);

  const updateCart = (items) => {
    setCartItems(items);
    localStorage.setItem(`cart_${userId}`, JSON.stringify(items));
  };

  const handleQuantityChange = (id, delta) => {
    const updated = cartItems.map(item =>
      item._id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
    );
    updateCart(updated);
  };

  const removeItem = (id) => {
    const updated = cartItems.filter(item => item._id !== id);
    updateCart(updated);
    setSelectedItems(prev => prev.filter(i => i !== id));
  };

  const handleCheckout = async () => {
    if (!userId) return alert("Bạn cần đăng nhập để thanh toán");
    if (!fullName || !email || !phone || !address) return setMessage("Vui lòng điền đủ thông tin khách hàng");

    const orderData = {
      items: cartItems.filter(i => selectedItems.includes(i._id)).map(i => ({ productId: i._id, quantity: i.quantity, price: i.price })),
      fullName, email, phone, address, note
    };

    try {
      await createOrderWithDetails(orderData);
      setMessage("✅ Đặt hàng thành công!");
      const newCart = cartItems.filter(item => !selectedItems.includes(item._id));
      updateCart(newCart);
    } catch (e) {
      setMessage("❌ Lỗi khi đặt hàng: " + (e.message || "Không xác định"));
    }
  };

  if (!userId) return <p>⚠ Bạn cần đăng nhập để sử dụng giỏ hàng.</p>;

  return (
    <div style={{ padding: 20 }}>
      <h2>🛒 Giỏ hàng</h2>
      {cartItems.map(item => (
        <div key={item._id}>
          <input type="checkbox" checked={selectedItems.includes(item._id)} onChange={() => setSelectedItems(prev => prev.includes(item._id) ? prev.filter(i => i !== item._id) : [...prev, item._id])} />
          {item.name} - {item.quantity} x {item.price} VND
          <button onClick={() => handleQuantityChange(item._id, 1)}>+</button>
          <button onClick={() => handleQuantityChange(item._id, -1)}>-</button>
          <button onClick={() => removeItem(item._id)}>🗑 Xóa</button>
        </div>
      ))}

      <h3>📋 Thông tin khách hàng</h3>
      <input placeholder="Họ tên" value={fullName} onChange={(e) => setFullName(e.target.value)} />
      <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input placeholder="SĐT" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <input placeholder="Địa chỉ" value={address} onChange={(e) => setAddress(e.target.value)} />
      <textarea placeholder="Ghi chú" value={note} onChange={(e) => setNote(e.target.value)}></textarea>
      <button onClick={handleCheckout}>Thanh toán</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CartPage;
