import React, { useEffect, useState } from "react";
import axios from "axios";

const ReviewPage = () => {
    const [reviews, setReviews] = useState([]);

    const fetchReviews = async () => {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/products/reviews/all", {
            headers: { Authorization: `Bearer ${token}` },
        });
        setReviews(res.data);
    };

    const handleDelete = async (productId, reviewId) => {
        const token = localStorage.getItem("token");
        await axios.delete(`http://localhost:5000/api/products/${productId}/reviews/${reviewId}`, {
            headers: { Authorization: `Bearer ${token}` },
        });
        fetchReviews(); // refresh
    };

    useEffect(() => {
        fetchReviews();
    }, []);

    return (
        <div>
            <h2>Quản lý đánh giá</h2>
            <table>
                <thead>
                    <tr>
                        <th>Sản phẩm</th>
                        <th>Người dùng</th>
                        <th>Đánh giá</th>
                        <th>Nội dung</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {reviews.map((r) => (
                        <tr key={r._id}>
                            <td>{r.productName}</td>
                            <td>{r.userId?.username || "Ẩn danh"}</td>
                            <td>{r.rating} ⭐</td>
                            <td>{r.comment}</td>
                            <td>
                                <button onClick={() => handleDelete(r.productId, r._id)}>❌ Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ReviewPage;
