import React, { useEffect, useState } from "react";
import { getBrands, createBrand, deleteBrand } from "../../../services/brandService";

const BrandPage = () => {
    const [brands, setBrands] = useState([]);
    const [newBrand, setNewBrand] = useState("");

    useEffect(() => {
        fetchBrands();
    }, []);

    const fetchBrands = async () => {
        setBrands(await getBrands());
    };

    const handleCreateBrand = async () => {
        if (!newBrand) return alert("Bạn cần nhập tên thương hiệu!");
        try {
            await createBrand({ name: newBrand });
            setNewBrand("");
            fetchBrands();
        } catch (error) {
            alert("Không thể thêm Brand. Kiểm tra log!");
        }
    };

    return (
        <div>
            <h2>Manage Brands</h2>
            <input placeholder="Brand Name" value={newBrand} onChange={(e) => setNewBrand(e.target.value)} />
            <button onClick={handleCreateBrand}>Add Brand</button>
            <ul>
                {brands.map((brand) => (
                    <li key={brand._id}>
                        {brand.name}
                        <button onClick={() => deleteBrand(brand._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BrandPage;
